package misc;

import java.util.Scanner;

public class Warmup1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner input = new Scanner(System.in);
		System.out.println("Enter a number:");
		
		int n= input.nextInt();
		                         
		for(int i=0; i<=n; i++) {
			System.out.println(i);
		}
		
		for(int j=n; j >= 0; j--) {
			System.out.print(j);
		}
	
		
	}	
		
	

	}


